sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
  if (isLoading || newValue !== 'Night') {
    return;
  }

  var now = new Date();

  // Set "available_from" to today at 8:00 PM
  var fromDate = new Date(now);
  fromDate.setHours(20, 0, 0, 0); // 8:00 PM

  // Set "available_to" to tomorrow at 6:00 AM
  var toDate = new Date(now);
  toDate.setDate(toDate.getDate() + 1); // next day
  toDate.setHours(6, 0, 0, 0); // 6:00 AM

  // Convert to Glide-compatible format
  var fromString = fromDate.toISOString().slice(0, 19);
  var toString = toDate.toISOString().slice(0, 19);

  g_form.setValue('available_from', fromString);
  g_form.setValue('available_to', toString);
}
